// It converts newlines to the correct format and stringifies the content
function formatWhatsAppMessage(items) {
  try {
    // Get the phone number from the AI Agent7 node
    const phoneNumber = $('AI Agent7').first().json.output;

    // Get the customer details from Webhook3
    const customerName = $('Webhook3').first().json.body.name;
    const customerBudget = $('Webhook3').first().json.body.budget;
    const visitDate = $('Webhook3').first().json.body.visit;

    // For each item in the incoming data
    for (const item of items) {
      // Check if output property exists
      if (item.json.output) {
        // Get the message text
        let messageText = item.json.output;

        // Replace placeholders with actual customer details
        messageText = messageText.replace(/\[Name\]/g, customerName);
        messageText = messageText.replace(/\[Budget\]/g, customerBudget);  // Note the capital B in Budget
        messageText = messageText.replace(/\[Visit Date\]/g, visitDate);  // Note the space and capitalization

        // Ensure all newlines are properly formatted for WhatsApp
        // First standardize all newlines to \n
        messageText = messageText.replace(/\r\n/g, '\n');

        // Create a properly formatted message object for WhatsApp API
        item.json.whatsappMessage = {
          messaging_product: "whatsapp",
          recipient_type: "individual",
          to: phoneNumber,
          type: "text",
          text: {
            preview_url: false,
            body: messageText  // This should now have all placeholders replaced
          }
        };

        // Add customer details to the item for later use
        item.json.customerDetails = {
          name: customerName,
          budget: customerBudget,
          visitDate: visitDate
        };

        // If you need the stringified version for some reason
        item.json.stringifiedMessage = JSON.stringify(item.json.whatsappMessage);
      } else {
        // Handle cases where output is missing
        item.json.whatsappMessage = {
          text: "No message content found."
        };
        item.json.stringifiedMessage = JSON.stringify({
          text: "No message content found."
        });
      }
    }

    return items;
  } catch (error) {
    // Add error handling
    const errorItem = {
      json: {
        error: true,
        errorMessage: `Error processing WhatsApp message: ${error.message}`
      }
    };
    return [errorItem];
  }
}
